package com.urooj.carpoolingapp.model;

import androidx.annotation.Keep;

import com.google.firebase.database.IgnoreExtraProperties;

@IgnoreExtraProperties
@Keep
public class User {
 private String userId;
 private String email;
 private String fullName;
 private String phoneNumber;
 private String userType; // "driver" or "passenger"

 // Common fields for both users
 private String caste;
 private String cnic;
 private String gender; // "male" or "female"

 // Additional fields for drivers
 private String religion;
 private String nationality;
 private String carNumber;
 private String homeAddress;
 private String driverPhotoUrl;
 private String licensePhotoUrl;

 // Profile fields
 private String profileImageUrl;
 private String address;

 // Default constructor required for Firebase
 public User() {
 // Default empty constructor required for Firebase
 this.profileImageUrl = "";
 this.address = "";
 }

 // Constructor for Passenger
 public User(String userId, String email, String fullName, String phoneNumber,
 String caste, String cnic, String gender) {
 this.userId = userId;
 this.email = email;
 this.fullName = fullName;
 this.phoneNumber = phoneNumber;
 this.userType = "passenger";
 this.caste = caste;
 this.cnic = cnic;
 this.gender = gender;
 this.profileImageUrl = "";
 this.address = "";
 }

 // Constructor for Driver
 public User(String userId, String email, String fullName, String phoneNumber,
