 this.religion = religion;
 this.nationality = nationality;
 this.carNumber = carNumber;
 this.homeAddress = homeAddress;
 this.profileImageUrl = "";
 this.address = homeAddress;
 }

 // Getters and Setters
 public String getUserId() {
 return userId;
 }

