+ .show();
+
+ // Close activity after a delay
+ requestRideButton.postDelayed(this::finish, 2000);
+ })
+ .addOnFailureListener(e -> {
+ showError("Failed to send request: " + e.getMessage());
+ requestRideButton.setEnabled(true);
+ });
+ } else {
+ showError("Failed to create request ID");
+ requestRideButton.setEnabled(true);
+ }
+ }
+
+ private void createDriverNotification(String requestId) {
+ // Create a notification for the driver
+ Map<String, Object> notification = new HashMap<>();
+ notification.put("type", "ride_request");
+ notification.put("requestId", requestId);
+ notification.put("timestamp", new SimpleDateFormat("yyyy-MM-dd HH:mm:ss", Locale.getDefault()).format(new Date()));
+ notification.put("message", "New ride request from " + mAuth.getCurrentUser().getEmail());
+ notification.put("read", false);
+
+ mDatabase.child("notifications").child(driverId).push().setValue(notification);
+ }
+
+ private void showError(String message) {
+ Toast.makeText(this, message, Toast.LENGTH_SHORT).show();
+ }
+}
